if __name__ == '__main__':
    #生成parkSession对象
    spark = SparkSession.builder.appName("Recommonder").master("local[*]").getOrCreate()
    sc = spark.sparkContext

    # rdd = sc.textFile('hdfs://node1:8020/input/000000_0')
    rdd = sc.textFile('./000000_0')

    #第一阶段 -- 去掉:1
    #第一步 --按\t拆分行
    line_rdd = rdd.map(lambda line:line.split("\t"))
    # print('line_rdd: ',line_rdd.collect())

    #获取label标签数据并修改内容值
    label_rdd = rdd.map(lambda line:line.split("\t")[0])
    label_rdd = label_rdd.map(lambda value: 0.0 if float(value) == -1.0 else float(value))
    # print('label:',label_rdd.collect())

    #获取其他维度数据,并按;拆分
    line_rdd2 = line_rdd.map(lambda line:line[1].split(";"))
    # print('line_rdd2: ',line_rdd2.collect())

    # 获取
    word_rdd= line_rdd2.flatMap(lambda x:x )
    dimension_rdd= word_rdd.map(lambda x:x.split(':')[0] )
    # print('dimension_rdd: ', dimension_rdd.collect())

    # 将所有元素压平，得到的是所有分特征，然后去重，最后索引化，也就是加上下标，最后转成map是为了后面查询用 Item.id,hitop_id55 Item.screen,screen4
    dimension_rdd_index = dimension_rdd.distinct().zipWithIndex().collectAsMap()
    # print('dimension_rdd_index: ',dimension_rdd_index) # dist
    # 所有维度:{'Item.screen,screen4': 0, 'Item.network,x': 1, 'Item.icount,6': 2, 'Item.fsize,2': 3, 'Item.ischarge,0': 4, 'Item.downNum,50': 5, 'User.Item*Item,hitop_id22*hitop_id6': 6, 'User.pay*Item.price,pay_ability1*4593': 7, 'User.Item*Item,hitop_id12*hitop_id6': 8, 'User.Item*Item,hitop_id98*hitop_id6': 9, 'User.phone*Item,device_name10*hitop_id6': 10, 'Item.id,hitop_id6': 11, 'Item.name,ch_name89': 12, 'All,0': 13, 'Item.author,author19': 14, 'Item.sversion,sversion0': 15, 'Item.dgner,designer18': 16, 'Item.stars,5.56': 17, 'Item.comNum,11': 18, 'Item.font,font5': 19, 'Item.price,4593': 20, 'User.Item*Item,hitop_id10*hitop_id6': 21, 'User.Item*Item,hitop_id27*hitop_id6': 22, 'User.phone*Item,device_name311*hitop_id6': 23, 'User.Item*Item,hitop_id17*hitop_id6': 24}

    # 制作稀疏变量
    # 制作 行数据>>字典数据
    # line_rdd2: [['Item.id,hitop_id6:1', 'Item.screen,screen4:1'],['Item.id,hitop_id6:1', 'Item.screen,screen4:1']]
    def list_dist(data):
        result = {}
        for i in data:
            result[i.split(":")[0]] = i.split(":")[1]
        return result
    line_dist_rdd = line_rdd2.map(list_dist)

    # 每行匹配固定维度,字典只保留value
    def match_data(data):
        for k, v in dimension_rdd_index.items():
            if k in data.keys(): data[k] = 1
            elif k not in data.keys(): data[k] = 0
        return data.values()
    all_value_rdd = line_dist_rdd.map(match_data).map(lambda value:list(value))
